export default function AnimatedVector() {
  return (
    <div style={{display:"flex", alignItems:"center", justifyContent:"center", minHeight:"100vh", background:"#000"}}>
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox={`0 0 ${VIEW_W} ${VIEW_H}`}
        width="700"
        height="700"
        fill="none"
      >
        {paths.map((p, i) => {
          // compute numeric strokeWidth for overlay to make visible
          const swNum = Number(p.strokeWidth) || 1;
          const overlayWidth = Math.max(swNum, 3);
          return (
            <g key={i}>
              {p.fill && <path d={p.d} fill={p.fill} />}
              <motion.path
                d={p.d}
                stroke={p.stroke || p.fill || "#ffffff"}
                strokeWidth={overlayWidth}
                strokeLinecap="round"
                strokeLinejoin="round"
                fill="none"
                initial={{ pathLength: 0 }}
                animate={{ pathLength: 1 }}
                transition={{
                  duration: 2.2,
                  delay: i * 0.12,
                  repeat: Infinity,
                  repeatDelay: 1.0,
                  ease: "easeInOut"
                }}
              />
            </g>
          );
        })}
      </svg>
    </div>
  );
}
